  `reply` text,
  `userid` int(10) unsigned NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=3 DEFAULT CHARSET=utf8 ;

# 表的结构 si_honor 
DROP TABLE IF EXISTS `si_honor`;
CREATE TABLE `si_honor` (
