INSERT INTO `si_category` VALUES ('17','企业文化','Culture','/public/home/default/img/introduce/introduce03.png','11','34','0','','','核心价值观--以人为本、持续创新、安全缔造信息未来\r\n经营理念\r\n团队精神--平等、尊重、协作、务实\r\n员工品质标准','','List_culture.html','Show_article.html','<p>核心价值观――以人为本、持续创新、安全缔造信息未来</p><p>&nbsp; &nbsp; &nbsp;以人为本：</p><p>&nbsp; &nbsp; &nbsp; • &nbsp;人力资源是企业的第一资源；</p><p>&nbsp; &nbsp; &nbsp; • &nbsp;尊重每个员工的个性发展，努力开发每个员工的潜能、帮助员工成长；</p><p>&nbsp; &nbsp; &nbsp; • &nbsp;尊重每个员工对社会和公司所作的贡献并予以公平回报；</p><p>&nbsp; &nbsp; &nbsp;持续创新：</p><p>&nbsp; &nbsp; &nbsp;• &nbsp;自主持续创新是海峡信息不断成就辉煌的不竭动力；</p><p>&nbsp; &nbsp; &nbsp;• &nbsp;创新不仅表现在技术研发领域，还包括探索新的管理模式、改进新的工作方法、</p><p>&nbsp; &nbsp; &nbsp; &nbsp;提高工作效率、合理化流程再造等；</p><p>&nbsp; &nbsp; &nbsp;• &nbsp;学习是持续创新的源动力，海峡信息努力构建学习型组织，鼓励每个员工任何</p><p>&nbsp; &nbsp; &nbsp; &nbsp;时间、任何地点、任何方式学习；&nbsp;</p><p><br/></p><p>&nbsp; 经营理念</p><p>&nbsp; &nbsp; &nbsp; • &nbsp;为客户创造价值，以服务赢得市场；</p><p>&nbsp; &nbsp; &nbsp; • &nbsp;员工、企业、社会三者共赢；</p><p>&nbsp; &nbsp; &nbsp; • &nbsp;群策群力，追求卓越；&nbsp;</p><p><br/></p><p>&nbsp;团队精神――平等、尊重、协作、务实</p><p>&nbsp; &nbsp; &nbsp; • &nbsp;平等：团队成员没有高低贵贱之分，只有分工不同；</p><p>&nbsp; &nbsp; &nbsp; • &nbsp;尊重：团队严谨自律，尊重他人的工作方式；</p><p>&nbsp; &nbsp; &nbsp; • &nbsp;协作：拥有统一的价值观，互信协作；</p><p>&nbsp; &nbsp; &nbsp; • &nbsp;务实：作正确的事情，注重效益；&nbsp;</p><p><br/></p><p>&nbsp;员工品质标准</p><p>&nbsp; &nbsp; &nbsp; • &nbsp;负责任的态度；</p><p>&nbsp; &nbsp; &nbsp; • &nbsp;艰苦奋斗的精神；</p><p>&nbsp; &nbsp; &nbsp; • &nbsp;宽容的心态；</p><p>&nbsp; &nbsp; &nbsp; • &nbsp;感恩的心情；</p><p><br/></p><p>&nbsp;人才经营理念</p><p>&nbsp; &nbsp; &nbsp; • &nbsp;人力资源是企业的第一资源；&nbsp;</p><p>&nbsp; &nbsp; &nbsp; • &nbsp;德才兼备，德为上；唯才是举，不以学历作为人才唯一评价尺度 ；</p><p>&nbsp; &nbsp; &nbsp; • &nbsp;尊重员工、信任员工；没有不合适的员工，只有不合适的岗位；</p><p>&nbsp; &nbsp; &nbsp; • &nbsp;帮助、激励员工持续学习，让员工与企业一起成长；&nbsp;</p><p><br/></p>','1','3','/public/home/default/img/introduce/introduce03.png');
